mapper.update:
update tb_user_transaction_monitor 
set trans_count = trans_count + 1,
    trans_hashs = #{transHashs},
    trans_hash_lastest = #{transHashLastest},
    modify_time = #{modifyTime}
where user_name = #{userName}
  and network_id = #{networkId}
  and contract_name = #{contractName}
  and DATE_FORMAT(create_time,'%Y-%m-%d %H:%i:%s') = DATE_FORMAT(#{createTime},'%Y-%m-%d %H:%i:%s')
  and user_type = #{userType}
  and contract_address = #{contractAddress}
  and interface_name = #{interfaceName}
  and trans_type = #{transType}
  and trans_unusual_type = #{transUnusualType}

update tb_user_transaction_monitor 
set user_name = #{userName},
    user_type = 0
where user_name = #{address}
  and network_id = #{networkId}
  and user_type = 1

update tb_user_transaction_monitor 
set contract_name = #{contractName},
    interface_name = #{interfaceName},
    trans_unusual_type = #{transUnusualType}
where contract_name = #{contractBin}
  and network_id = #{networkId}
  and (trans_unusual_type = 1 or trans_unusual_type = 2)

mapper.select:
select trans_count as transCount,
       trans_hashs as transHashs
from tb_user_transaction_monitor 
where user_name = #{userName}
  and network_id = #{networkId}
  and contract_name = #{contractName}
  and DATE_FORMAT(create_time,'%Y-%m-%d %H:%i:%s') = DATE_FORMAT(#{createTime},'%Y-%m-%d %H:%i:%s')
  and user_type = #{userType}
  and contract_address = #{contractAddress}
  and interface_name = #{interfaceName}
  and trans_type = #{transType}
  and trans_unusual_type = #{transUnusualType}

select distinct user_name as userName, user_type as userType from tb_user_transaction_monitor where 1=1
and network_id = #{networkId}
order by modify_time

select distinct interface_name as interfaceName, trans_unusual_type as transUnusualType from tb_user_transaction_monitor where 1=1
and network_id = #{networkId}
and user_name = #{userName}
order by modify_time

select IFNULL(sum(trans_count),0) from tb_user_transaction_monitor where 1=1

  and network_id = #{networkId}


  and user_name = #{userName}


   and DATE_FORMAT(modify_time,'%Y-%m-%d') >= STR_TO_DATE(#{startDate},'%Y-%m-%d') 


   and DATE_FORMAT(modify_time,'%Y-%m-%d') <= STR_TO_DATE(#{endDate},'%Y-%m-%d') 


  and interface_name = #{interfaceName}

select trans_count as transCount, modify_time as time from tb_user_transaction_monitor where 1=1
and network_id = #{networkId}
and user_name = #{userName}
and DATE_FORMAT(modify_time,'%Y-%m-%d') >= STR_TO_DATE(#{startDate},'%Y-%m-%d')
and DATE_FORMAT(modify_time,'%Y-%m-%d') <= STR_TO_DATE(#{endDate},'%Y-%m-%d')
and interface_name = #{interfaceName}
order by modify_time

select count(distinct user_name) from tb_user_transaction_monitor where user_type=1
and network_id = #{networkId}
and user_name like CONCAT('%',#{userName},'%')

select t.user_name as userName, sum(t.trans_count) as transCount, t.trans_hashs as hashs, t.modify_time as time from ( select user_name, trans_count, trans_hashs, modify_time from tb_user_transaction_monitor where user_type=1
and network_id = #{networkId}
and user_name like CONCAT('%',#{userName},'%')
order by user_name, modify_time desc ) t group by t.user_name
limit #{start},#{pageSize}

select count(distinct contract_address) from tb_user_transaction_monitor where trans_unusual_type=1
and network_id = #{networkId}
and contract_address like CONCAT('%',#{contractAddress},'%')

select t.contract_name as contractName, t.contract_address as contractAddress, sum(t.trans_count) as transCount, t.trans_hashs as hashs, t.modify_time as time from ( select contract_name, contract_address, trans_count, trans_hashs, modify_time from tb_user_transaction_monitor where trans_unusual_type=1
and network_id = #{networkId}
and contract_address like CONCAT('%',#{contractAddress},'%')
order by contract_address, modify_time desc ) t group by t.contract_address
limit #{start},#{pageSize}

select trans_hash_lastest
from tb_user_transaction_monitor 
where contract_name = #{contractBin}
  and network_id = #{networkId}
  and (trans_unusual_type = 1 or trans_unusual_type = 2)
group by contract_name

mapper.delete:
DELETE from tb_user_transaction_monitor where network_id = #{networkId} and user_type=0 and trans_unusual_type=0
and modify_time not in(
 select t.modify_time from(
   select modify_time from tb_user_transaction_monitor where network_id = #{networkId} ORDER BY modify_time DESC LIMIT ${monitorInfoRetainMax}
   ) t
 );
